def get_data(input_candidate):

    candidate_data = pd.read_json(input_candidate)
    resume = list(candidate_data['resume'])[0][0]

    url = resume['url']

    ##print(url,'\n\n')
    ##print(job_data['skills'])

    #Request for resume from server
    resume = requests.get(url)
    f = open('dummy_res.pdf', 'wb').write(resume.content)
    document = open('dummy_res.pdf', 'rb')
    
    #document = open('resume.pdf', 'rb')
    boxes = []
    extracted_text = ""
    data = []

    #Create resource manager
    rsrcmgr = PDFResourceManager()
    # Set parameters for analysis.
    laparams = LAParams()
    # Create a PDF page aggregator object.
    device = PDFPageAggregator(rsrcmgr, laparams=laparams)
    interpreter = PDFPageInterpreter(rsrcmgr, device)
    for page in PDFPage.get_pages(document):
        interpreter.process_page(page)
        # receive the LTPage object for the page.
        layout = device.get_result()
        x0 = y0 = x1 = y1 = 0
        tableColumns = []
        for element in layout:
            if isinstance(element, LTTextBoxHorizontal):
                data.append(str(element.get_text()).lower().strip())
                boxes.append(element.get_text())
                extracted_text += element.get_text()
                x0,y0,x1,y1=element.bbox

    #print(extracted_text)
    return extracted_text



def get_locations(raw_text):
    
    ###=======================  LOCATION MATCHER  ==================================
    ##
    ##
    nlp = spacy.load('en_core_web_sm')

    # Pass Data theough NLP pipeline
    document = nlp(raw_text)
    
    # Making a List of Entities ( CHUNKS )
    entities = document.ents
        
    ##for entities in entities_list:
    ##    for ent in entities:
    ##        print(ent.text, ent.start_char, ent.end_char, ent.label_)


    # Finding Locations in The PDF.
    locations = []

    for ent in entities:
        if ent.label_ == 'GPE':
            locations.append(ent.text)
        
    while locations.count('\n'):
        locations.remove('\n')
    #print(locations)
    
    
    locs_df = pd.read_csv('cities.csv', header=None)

    locs_dataset = list(locs_df[0].values)
    locs_dataset.extend(list(set(locs_df[1].values)))

    location_matcher = Matcher(nlp.vocab)

    for loc in locs_dataset:
        pattern = [{'LOWER':loc.lower()}]
        location_matcher.add(loc, None, pattern)

    loc_matched = location_matcher(document)
    
    loc_names = []
    for i in loc_matched:
        loc_names.append(nlp.vocab.strings[i[0]])

    locations.extend(loc_names)

    locations = list(set(locations))
  
    return locations


